mapper.update:
UPDATE
seckill
SET
number = number -1
WHERE seckill_id = #{seckillId,jdbcType=BIGINT}
AND start_time  <=  #{killTime,jdbcType=TIMESTAMP}
AND end_time  >=  #{killTime,jdbcType=TIMESTAMP}
AND number  >  0

UPDATE
seckill
SET
number = number -1
WHERE seckill_id = #{seckillId,jdbcType=BIGINT}
AND start_time  <=  #{killTime,jdbcType=TIMESTAMP}
AND end_time  >=  #{killTime,jdbcType=TIMESTAMP}
AND number  >  0 AND number=#{number,jdbcType=INTEGER}
